const basePatterns = [
    [5, 0, 0, 0, 2, 5],
    [0, 0, 0, 5, 0, 5],
    [0, 0, 0, 2, 2, 5],
    [5, 0, 0, 0, 0, 2],
    [5, 0, 0, 2, 2, 0],
    [0, 0, 0, 0, 5, 0],
    [5, 0, 0, 5, 5, 2],
];

const rand = () => Math.floor(Math.random() * basePatterns.length);

const generateWeek = () =>
    basePatterns[rand()]
        .concat(basePatterns[rand()])
        .concat(basePatterns[rand()])
        .concat(basePatterns[rand()])
        .concat(basePatterns[rand()])
        .concat(basePatterns[rand()])
        .concat(basePatterns[rand()]);

